CustomField.formula:
IF(ISNULL(BMCServiceDesk__Scheduled_End_Date__c), "YES",
	(IF ( ISNULL(BMCServiceDesk__Actual_End_Date__c) && ISNULL(BMCServiceDesk__Closed_Date_Time__c),( IF(BMCServiceDesk__Scheduled_End_Date__c >= NOW(), "YES", "NO")), 
		( IF (ISNULL(BMCServiceDesk__Actual_End_Date__c) && NOT(ISNULL(BMCServiceDesk__Closed_Date_Time__c)), ( IF(BMCServiceDesk__Scheduled_End_Date__c >= BMCServiceDesk__Closed_Date_Time__c, "YES", "NO")), 
				(IF(NOT(ISNULL(BMCServiceDesk__Actual_End_Date__c)) && ISNULL( BMCServiceDesk__Closed_Date_Time__c),(IF(BMCServiceDesk__Scheduled_End_Date__c >= BMCServiceDesk__Actual_End_Date__c, "YES", "NO")),
					(IF(BMCServiceDesk__Scheduled_End_Date__c >= BMCServiceDesk__Actual_End_Date__c, "YES", "NO"))
					)
				) 
			)
		)
	  )
	)
)
