CustomField.formula:
IF( ISPICKVAL(BMCServiceDesk__Condition__c, 'Service Target is Met'), 'MET', 
    IF( ISPICKVAL(BMCServiceDesk__Condition__c,'Service Target is Missed'), 'MISS', 
        IF( ISPICKVAL(BMCServiceDesk__Condition__c,'Service Target Warning Occurs'), 'WARNING',
            IF( ISPICKVAL(BMCServiceDesk__Condition__c,'Service Target is Canceled'), 'CANCEL', 'TIME')
        )
    ) 
)
