WebLink.url:
{!REQUIRESCRIPT("/soap/ajax/8.0/connection.js")}
var link = "{!Loop__Document_Request__c.Loop__Email_Link__c}";
if (link == "")
{
    alert("Document Request status is still pending.");
}
else
{
    var id = "{!Loop__Document_Request__c.Id}";
    var request = new sforce.SObject("Loop__Document_Request__c");
    request.Id = id;
    request.Loop__Status__c = 'Emailed';
    sforce.connection.update([request]);
    window.location.href = link;
}
